    template <int i>
    inline void precompute() {
        std::cout << std::setprecision(20) << std::endl;
        std::cout << "\ttemplate<> struct container<" << i << "> {\n";
        std::cout << "\t\tstatic constexpr std::array<HHG::h_float, " << i/2 << "> abscissa = {\n\t\t\t";
        for(const auto& x : boost::math::quadrature::gauss<long double, i>::abscissa()) { std::cout << x << "L, "; }
        std::cout << "\n\t\t};\n";
        std::cout << "\t\tstatic constexpr std::array<HHG::h_float, " << i/2 << "> weights = {\n\t\t\t";
        for(const auto& x : boost::math::quadrature::gauss<long double, i>::weights()) { std::cout << x << "L, "; }
        std::cout << "\n\t\t};\n\t};" << std::endl;
    }

